import com.example.equabase.SubCategoryType
import com.example.equabase.data.PhysicsFormula

object ElectrostaticsFormulas {

    private fun formula(
        name: String,
        formula: String,
        description: String
    ) = PhysicsFormula(name, formula, description, SubCategoryType.ELECTROSTATICS)

    val list = listOf(
        formula("Електричний заряд", "q = n*e", "q — заряд, n — кількість елементарних зарядів, e — елементарний заряд."),
        formula("Закон Кулона", "F = {k*q1*q2}/r^2", "F — сила взаємодії між двома зарядами q1 і q2, розташованими на відстані r."),
        formula("Постійна Кулона", "k = 1/{4*π*ε0}", "k — коефіцієнт пропорційності, ε0 — електрична стала."),
        formula("Відносна діелектрична проникність", "ε = F_vac/F_med", "ε — відносна діелектрична проникність середовища."),
        formula("Напруженість електричного поля", "E = F/q", "E — напруженість електричного поля, F — сила, q — заряд."),
        formula("Напруженість поля точкового заряду у вакуумі", "E = {k*q}/r^2", "E — напруженість поля точкового заряду q на відстані r."),
        formula("Напруженість поля точкового заряду в середовищі", "E = {k*q}/{ε*r^2}", "E — напруженість поля в середовищі з діелектричною проникністю ε."),
        formula("Напруженість поля поза зарядженою сферою", "E = {k*q}/r^2", "E — напруженість поля на відстані r від центра зарядженої сфери."),
        formula("Напруженість поля поза зарядженою сферою", "E = {k*σ4*π*R^2}/r^2", "E — напруженість поля на відстані r від центра зарядженої сфери."),
        formula("Напруженість поля нескінченної зарядженої площини", "E = σ/{2*ε0}", "E — напруженість поля, σ — поверхнева густина заряду."),
        formula("Напруженість поля конденсатора", "E = U/d", "E — напруженість поля між пластинами конденсатора, U — напруга, d — відстань між пластинами."),
        formula("Робота в електричному полі", "A = F*Δd", "A — робота, F — сила, Δd — переміщення."),
        formula("Потенціальна енергія двох точкових зарядів", "W = {k*q1*q2}/{ε*r}", "W — потенціальна енергія взаємодії двох зарядів q1 і q2 на відстані r."),
        formula("Робота в електричному полі (через потенціальну енергію)", "A = W1 - W2", "A — робота, W1 і W2 — потенціальні енергії в початковій і кінцевій точках."),
        formula("Потенціал електростатичного поля", "φ = W/q", "φ — потенціал, W — потенціальна енергія, q — заряд."),
        formula("Напруга (різниця потенціалів)", "U = φ1 - φ2", "U — напруга між двома точками з потенціалами φ1 і φ2."),
        formula("Робота перенесення заряду", "A = q*U", "A — робота, q — заряд, U — напруга."),
        formula("Потенціал поля точкового заряду", "φ = {k*q}/{ε*r}", "φ — потенціал на відстані r від точкового заряду q."),
        formula("Напруженість електростатичного поля", "E = U/Δd", "E — напруженість поля, U — напруга, Δd — відстань."),
        formula("Результуюче електричне поле", "E = E1 + E2", "E — результуюча напруженість поля від двох полів E1 і E2."),
        formula("Електричний момент диполя", "p = q*l", "p — електричний момент, q — заряд, l — відстань між зарядами."),
        formula("Електрична ємність", "C = q/φ", "C — електрична ємність, q — заряд, φ — потенціал."),
        formula("Ємність кулі", "C = {ε*R}/k", "C — ємність кулі радіуса R в середовищі з діелектричною проникністю ε."),
        formula("Ємність двох провідників", "C = q/U", "C — ємність системи двох провідників, q — заряд, U — напруга."),
        formula("Ємність плоского конденсатора", "C = {ε*ε0*S}/d", "C — ємність, ε — діелектрична проникність, ε0 — електрична стала, S — площа пластин, d — відстань між ними."),
        formula("Ємність сферичного конденсатора", "C = {4*π*ε*ε0*R1*R2}/{R2 - R1}", "C — ємність сферичного конденсатора з радіусами R1 і R2."),
        formula("Потенціальна енергія зарядженого конденсатора", "W = q*E*d", "W — потенціальна енергія, q — заряд, E — напруженість поля, d — відстань між пластинами."),
        formula("Потенціальна енергія зарядженого конденсатора (через напругу)", "W = {q*U}/2", "W — потенціальна енергія, q — заряд, U — напруга."),
        formula("Потенціальна енергія зарядженого конденсатора (через ємність)", "W = {C*U^2}/2", "W — потенціальна енергія, C — ємність, U — напруга."),
        formula("Потенціальна енергія зарядженого конденсатора (через заряд)", "W = q^2/{2*C}", "W — потенціальна енергія, q — заряд, C — ємність."),
        formula("Потенціальна енергія зарядженого конденсатора (через поле)", "W = {ε*ε0*E^2*V}/2", "W — потенціальна енергія, ε — діелектрична проникність, ε0 — електрична стала, E — напруженість поля, V — об'єм."),
        formula("Потенціальна енергія зарядженого конденсатора (через площу і відстань)", "W = {ε*ε0*E^2*S*d}/2", "W — потенціальна енергія, S — площа пластин, d — відстань між ними."),
        formula("Густина енергії електричного поля", "ω = W/V", "ω — густина енергії, W — потенціальна енергія, V — об'єм."),
        formula("Густина енергії електричного поля (через напруженість)", "ω = {ε0*ε*E^2}/2", "ω — густина енергії, ε0 — електрична стала, ε — діелектрична проникність, E — напруженість поля."),
        formula("Зв'язок між напругою і напруженістю", "U = E*d", "U — напруга, E — напруженість поля, d — відстань."),
        formula("Сила, що діє на заряд у полі", "F = q*E", "F — сила, q — заряд, E — напруженість поля."),
        formula("Енергія диполя в електричному полі", "W = -p*E*cos(θ)", "W — енергія диполя, p — електричний момент, E — напруженість поля, θ — кут між p і E."),
        formula("Момент сили, що діє на диполь", "M = p*E*sin(θ)", "M — момент сили, p — електричний момент, E — напруженість поля, θ — кут між p і E.")
    )
}