package com.example.equabase.data.formulas

import com.example.equabase.SubCategoryType
import com.example.equabase.data.PhysicsFormula

object KinematicsFormulas {

    private fun formula(
        name: String,
        formula: String,
        description: String
    ) = PhysicsFormula(name, formula, description, SubCategoryType.KINEMATICS)

    val list = listOf(
        formula("Швидкість", "v = s/t", "v — швидкість, s — шлях, t — час. Швидкість — це відношення шляху до часу."),
        formula("Шлях", "s = v*t", "s — шлях, v — швидкість, t — час. Шлях — це добуток швидкості на час."),
        formula("Прискорення", "a = (v - v_0)/t", "a — прискорення, v — кінцева швидкість, v_0 — початкова швидкість, t — час. Прискорення — це зміна швидкості з часом."),
        formula("Переміщення", "s = v_0*t + (1/2)*a*t^2", "s — переміщення, v_0 — початкова швидкість, a — прискорення, t — час. Формула переміщення при рівноприскореному русі."),
        formula("Кінцева швидкість", "v = v_0 + a*t", "v — кінцева швидкість, v_0 — початкова, a — прискорення, t — час. Формула рівноприскореного руху."),
        formula("Інша форма переміщення", "s = (v^2 - v_0^2)/(2*a)", "s — переміщення, v — кінцева швидкість, v_0 — початкова, a — прискорення. Формула без часу."),
        formula("Середня швидкість", "v̅ = (v_0 + v)/2", "v̅ — середня швидкість, v_0 — початкова, v — кінцева. Середня швидкість при рівноприскореному русі."),
        formula("Період обертання", "T = 1/ν", "T — період обертання, ν — частота. Період — обернена величина до частоти."),
        formula("Частота", "ν = N/t", "ν — частота, N — кількість обертів, t — час. Частота — це кількість обертів за одиницю часу."),
        formula("Кутова швидкість", "ω = φ/t", "ω — кутова швидкість, φ — кут повороту, t — час. Показує, як швидко змінюється кут."),
        formula("Зв’язок лінійної і кутової", "v = ω*R", "v — лінійна швидкість, ω — кутова, R — радіус. Визначає лінійну швидкість тіла, що обертається."),
        formula("Зв’язок кутової та частоти", "ω = 2*π*ν", "ω — кутова швидкість, ν — частота, π — число пі. Зв’язок між частотою та кутовою швидкістю."),
        formula("Довжина кола", "l = 2*π*R", "l — довжина кола, R — радіус, π — число пі. Довжина повного оберту навколо кола."),
        formula("Кутовий шлях", "φ = ω*t", "φ — кутовий шлях, ω — кутова швидкість, t — час. Скільки кутових радіан пройдено за час."),
        formula("Доцентрове прискорення (через v)", "a_ц = v^2/R", "a_ц — доцентрове прискорення, v — швидкість, R — радіус. Виникає при русі по колу."),
        formula("Доцентрове прискорення (через ω)", "a_ц = ω^2*R", "a_ц — доцентрове прискорення, ω — кутова швидкість, R — радіус. Інша форма через ω."),
        formula("Рівняння траєкторії", "y = x*tan(α) - (g*x^2)/(2*v_0^2*cos(α)^2)", "y — висота, x — дальність, α — кут, v_0 — початкова швидкість, g — прискорення вільного падіння. Траєкторія руху тіла при киданні під кутом."),
        formula("Час підйому", "t_п = v_0*sin(α)/g", "t_п — час підйому, v_0 — початкова швидкість, α — кут, g — прискорення вільного падіння. Час досягнення найвищої точки."),
        formula("Час польоту", "t = 2*v_0*sin(α)/g", "t — повний час польоту, v_0 — початкова швидкість, α — кут, g — прискорення вільного падіння."),
        formula("Максимальна висота", "h = v_0^2*sin(α)^2/(2*g)", "h — максимальна висота, v_0 — початкова швидкість, α — кут, g — прискорення вільного падіння."),
        formula("Дальність польоту", "L = v_0^2*sin(2*α)/g", "L — горизонтальна дальність, v_0 — початкова швидкість, α — кут, g — прискорення вільного падіння."),
        formula("Кут нахилу швидкості", "tan(β) = v_y/v_x", "β — кут нахилу, v_y — вертикальна складова швидкості, v_x — горизонтальна."),
        formula("Рівняння координат X", "x = v_0*cos(α)*t", "x — координата, v_0 — початкова швидкість, α — кут, t — час."),
        formula("Рівняння координат Y", "y = v_0*sin(α)*t - (g*t^2)/2", "y — координата, v_0 — початкова швидкість, α — кут, g — прискорення, t — час."),
        formula("Залежність швидкості від координати", "v^2 = v_0^2 + 2*a*s", "v — кінцева швидкість, v_0 — початкова, a — прискорення, s — переміщення."),
        formula("Шлях при рівномірному русі", "s = v̅*t", "s — шлях, v̅ — середня швидкість, t — час."),
        formula("Частота через період", "ν = 1/T", "ν — частота, T — період. Частота — обернена до періоду."),
        formula("Середня швидкість через шлях", "v̅ = s/t", "v̅ — середня швидкість, s — шлях, t — час.")
    )
}