import com.example.equabase.SubCategoryType
import com.example.equabase.data.PhysicsFormula

object MagneticFieldFormulas {

    private fun formula(
        name: String,
        formula: String,
        description: String
    ) = PhysicsFormula(name, """$$$formula$$""", description, SubCategoryType.FIELD)

    val list = listOf(
        formula("Сила Лоренца", "F = q * v * B * sin(α)", "F — сила, що діє на заряд q, який рухається зі швидкістю v в магнітному полі з індукцією B під кутом α."),
        formula("Сила Ампера", "F = I * l * B * sin(α)", "F — сила, що діє на провідник довжиною l зі струмом I в магнітному полі з індукцією B під кутом α."),
        formula("Магнітна індукція прямого провідника", "B = μ * μ0 * I / (2 * π * r)", "B — магнітна індукція на відстані r від прямого провідника зі струмом I; μ — відносна магнітна проникність; μ0 — магнітна постійна."),
        formula("Магнітна індукція котушки", "B = μ * μ0 * N * I / l", "B — магнітна індукція всередині котушки з N витками, довжиною l та струмом I."),
        formula("Магнітна індукція соленоїда", "B = μ * μ0 * n * I", "B — магнітна індукція всередині соленоїда з щільністю витків n та струмом I."),
        formula("Магнітна індукція тороїда", "B = μ * μ0 * N * I / (2 * π * R)", "B — магнітна індукція всередині тороїда з N витками, радіусом R та струмом I."),
        formula("Магнітна індукція кільцевого струму", "B = μ * μ0 * I / (2 * R)", "B — магнітна індукція в центрі кільця радіусом R зі струмом I."),
        formula("Магнітна індукція нескінченної площини", "B = μ * μ0 * I / (2 * π * r)", "B — магнітна індукція на відстані r від нескінченної площини зі струмом I."),
        formula("Магнітна постійна", "μ0 = 4 * π * 10^(-7)", "μ0 — магнітна постійна."),
        formula("Напруженість магнітного поля", "H = I / l", "H — напруженість магнітного поля; I — сила струму; l — довжина."),
        formula("Індукція магнітного поля", "B = μ0 * μ * H", "B — магнітна індукція; μ0 — магнітна постійна; μ — відносна магнітна проникність; H — напруженість магнітного поля."),
        formula("Максимальний момент магнітного поля", "M_max = B * I * S", "M — момент сили; B — магнітна індукція; I — сила струму; S — площа."),
        formula("Магнітна індукція", "M = I * S * B * sin(α)", "M — магнітний момент; I — сила струму; S — площа контура; B — магнітна індукція; α — кут."),
        formula("Момент однорідного магнітного поля", "p_m = I * S", "p_m — магнітний момент; I — сила струму; S — площа контура."),
        formula("Магнітне поле прямолінійного провідника кінцевої довжини із струмом", "B = μ * μ0 * I * (cos(α1) + cos(α2)) / (4 * π * r)", "B — магнітна індукція; μ — відносна магнітна проникність; μ0 — магнітна постійна; I — сила струму; r — відстань; α1, α2 — кути."),
        formula("Індукція магнітного поля, створеного нескінченно довгим прямим провідником із струмом", "B = μ * μ0 * I / (2 * π * r)", "B — магнітна індукція; μ — відносна магнітна проникність; μ0 — магнітна постійна; I — сила струму; r — відстань."),
        formula("Магнітна індукція поля в центрі кругового струму (витка)", "B = μ * μ0 * I / (2 * R)", "B — магнітна індукція; μ — відносна магнітна проникність; μ0 — магнітна постійна; I — сила струму; R — радіус."),
        formula("Напруженість магнітного поля нескінченної прямої дроту", "H = I / (2 * π * r)", "H — напруженість магнітного поля; I — сила струму; r — відстань."),
        formula("Напруженість магнітного поля в центрі витка", "H = I / (2 * R)", "H — напруженість магнітного поля; I — сила струму; R — радіус."),
        formula("Магнітна індукція соленоїда", "B = μ * μ0 * N * I / l", "B — магнітна індукція; μ — відносна магнітна проникність; μ0 — магнітна постійна; N — число витків; I — сила струму; l — довжина."),
        formula("Напруженість магнітного поля соленоїда", "H = N * I / l", "H — напруженість магнітного поля; N — число витків; I — сила струму; l — довжина."),
        formula("Магнітний потік і кут", "Φ = B * S * cos(α)", "Φ — магнітний потік; B — магнітна індукція; S — площа; α — кут."),
        formula("Магнітний потік", "Φ = B * S", "Φ — магнітний потік; B — магнітна індукція; S — площа."),
        formula("Сила Ампера", "F = I * l * B * sin(α)", "F — сила; I — сила струму; l — довжина; B — магнітна індукція; α — кут.")
    )
}